mapper.select:
SELECT COUNT(*)
FROM electronic_payment ep
LEFT JOIN employee e ON ep.employee_id = e.id
LEFT JOIN electronic_payment_category c ON ep.electronic_payment_category_id = c.id




                AND ep.employee_id = #{employeeId}


                AND (
                ep.employee_id = #{employeeId}
                OR EXISTS (
                SELECT 1
                FROM electronic_payment_process pp
                WHERE pp.electronic_payment_id = ep.id
                AND pp.employee_id = #{employeeId}
                )
                )




        AND (
        ep.title LIKE CONCAT('%', #{keyword}, '%')
        OR e.name LIKE CONCAT('%', #{keyword}, '%')
        )






                AND EXISTS (
                    SELECT 1
                    FROM electronic_payment_process pp
                    WHERE pp.electronic_payment_id = ep.id
                    AND pp.employee_id = #{employeeId}
                    AND pp.status = 1
                )


                AND ep.status = #{status}





        AND ep.status = 0
        AND EXISTS (
            SELECT 1
            FROM electronic_payment_process pp
            WHERE pp.electronic_payment_id = ep.id
            AND pp.employee_id = #{employeeId}
            AND pp.status = 0
            AND pp.step_order = (
                SELECT MIN(sub_pp.step_order)
                FROM electronic_payment_process sub_pp
                WHERE sub_pp.electronic_payment_id = ep.id
                AND sub_pp.status = 0
            )
        )


        AND c.name = #{category}

SELECT ep.id, ep.title, ep.created_at, CASE WHEN ep.priority = 0 THEN '긴급' WHEN ep.priority = 1 THEN '보통' ELSE '낮음' END AS priority, ep.amount, IFNULL(c.name, '기타') AS categoryName, IFNULL(e.name, '알수없음') AS drafterName, CASE WHEN ep.status = 0 THEN '대기중' WHEN ep.status = 1 THEN '승인' ELSE '반려' END AS status FROM electronic_payment ep LEFT JOIN employee e ON ep.employee_id = e.id LEFT JOIN electronic_payment_category c ON ep.electronic_payment_category_id = c.id
AND ep.employee_id = #{employeeId}
AND ( ep.employee_id = #{employeeId} OR EXISTS ( SELECT 1 FROM electronic_payment_process pp WHERE pp.electronic_payment_id = ep.id AND pp.employee_id = #{employeeId} ) )
AND ( ep.title LIKE CONCAT('%', #{keyword}, '%') OR e.name LIKE CONCAT('%', #{keyword}, '%') )
AND EXISTS ( SELECT 1 FROM electronic_payment_process pp WHERE pp.electronic_payment_id = ep.id AND pp.employee_id = #{employeeId} AND pp.status = 1 )
AND ep.status = #{status}
AND ep.status = 0 AND EXISTS ( SELECT 1 FROM electronic_payment_process pp WHERE pp.electronic_payment_id = ep.id AND pp.employee_id = #{employeeId} AND pp.status = 0 AND pp.step_order = ( SELECT MIN(sub_pp.step_order) FROM electronic_payment_process sub_pp WHERE sub_pp.electronic_payment_id = ep.id AND sub_pp.status = 0 ) )
AND c.name = #{category}
ORDER BY ep.id DESC LIMIT #{size} OFFSET #{offset}

SELECT e.id, e.name, IFNULL(j.name, '사원') AS jobTitle, IFNULL(d.dept_name, '소속없음') AS departmentName FROM employee e LEFT JOIN m_job j ON e.job_code = j.id LEFT JOIN m_department d ON e.dept_code = d.id
(e.name LIKE CONCAT('%', #{keyword}, '%') OR j.name LIKE CONCAT('%', #{keyword}, '%') OR d.dept_name LIKE CONCAT('%', #{keyword}, '%') )
AND e.id != #{myId}
